}
function toggleAlarm()
{
  alarmOn = !alarmOn;
  if (alarmOn)
  {
    $("#alarmButton").text('Alarm ON');
  }
  else
  {
    $("#alarmButton").text('Alarm OFF');
    $("#spectrogram").css('background','#9e988e');
  }

}

