template:
var numTraces = 4;
var deviceSelect = [];
var propSelect=[];
var attrSelect=[];
var valSelect=[];
var devInfo = [];

var plotStarted = false;
var plotTraces = [];
var plotStartTime = 0;
var plotLength = 0;
var plotLayout = {};

useWebSockets(true);
function onDocumentReady()
{
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        deviceSelect[itrace] = document.getElementById('deviceSelect' + itrace.toString());
        propSelect[itrace] = document.getElementById('propSelect' + itrace.toString());
        attrSelect[itrace] = document.getElementById('attrSelect' + itrace.toString());
        valSelect[itrace] = document.getElementById('valSelect' + itrace.toString());
        devInfo[itrace] = {name : '', prop : '', attr : '',device : {}, axis:'y1', pts:false};
        $('#unitSelect'+ itrace.toString()).html('');
    }
    setInterval(getDeviceValues, 1000);
}
function onWebSocketOpen()
{
    sendWebSocketMessageObject({topic : 'getDeviceList',payload : {}, 'userID' : userID,});
}
function onWebSocketClose()
{

}
function onWebSocketMessage(msg)
{
    if (msg.userID == userID)
    {
        switch(msg.topic)
        {
            case 'getDeviceList':
                loadDeviceList(msg.payload);
                break;
            case 'getAttrList':
                loadAttrList(msg);
                break;
            case 'getDeviceValues':
                loadDeviceValues(msg);
                break;
            default:
            // code block
        }
    }
}

function loadDeviceList(deviceList)
{
    deviceList.sort();
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        for (var ii = 0; ii < deviceList.length; ++ii)
        {
            var opt = document.createElement('option');
            opt.value = deviceList[ii];
            opt.innerHTML = deviceList[ii];
            deviceSelect[itrace].appendChild(opt);
        }
        var readOpt = document.createElement('option');
        readOpt.value = 'readings';
        readOpt.innerHTML = 'readings';
        propSelect[itrace].appendChild(readOpt);
        var setOpt = document.createElement('option');
        setOpt.value = 'settings';
        setOpt.innerHTML = 'settings';
        propSelect[itrace].appendChild(setOpt);
    }
}
function deviceSelected(itrace)
{
    propSelect[itrace].value = 'notSelected';
    clearAttrOptionList(itrace);
}
function propSelected(itrace)
{
    if (propSelect[itrace].value == 'notSelected') return;
    if(deviceSelect[itrace].value == 'notSelected')
    {
        propSelect[itrace].value = 'notSelected';
        return;
    }
    clearAttrOptionList(itrace);
    sendWebSocketMessageObject(
        {
            topic       : 'getAttrList',
            userID      : userID,
            trace       : itrace,
            name        : deviceSelect[itrace].value,
            prop        : propSelect[itrace].value
        });
}
function loadAttrList(data)
{
    data.attrList.sort();
    for (var ii = 0; ii < data.attrList.length; ++ii)
    {
        var opt = document.createElement('option');
        opt.value = data.attrList[ii];
        opt.innerHTML = data.attrList[ii];
        attrSelect[data.trace].appendChild(opt);
    }
}
function attrSelected(itrace)
{
    devInfo[itrace].name = '';
    devInfo[itrace].prop = '';
    devInfo[itrace].attr = '';
    devInfo[itrace].device = {};
    if (attrSelect[itrace].value == 'notSelected') 
    {
        $('#valueSelect' + itrace.toString()).html('');
        $('#unitSelect' + itrace.toString()).html('');
        return;
    }
    devInfo[itrace].name = deviceSelect[itrace].value;
    devInfo[itrace].prop = propSelect[itrace].value;
    devInfo[itrace].attr = attrSelect[itrace].value;


}
function clearAttrOptionList(itrace)
{
    devInfo[itrace].name = '';
    devInfo[itrace].prop = '';
    devInfo[itrace].attr = '';
    devInfo[itrace].device = {};

    while (attrSelect[itrace].firstChild)
    {
        attrSelect[itrace].removeChild(attrSelect[itrace].firstChild);
    }
    var opt = document.createElement('option');
    opt.value = 'notSelected';
    opt.innerHTML = '';
    attrSelect[itrace].appendChild(opt);
    $('#valueSelect' + itrace.toString()).html('');
    $('#unitSelect' + itrace.toString()).html('');
}
function getDeviceValues()
{
    var devList = [];
    var traceCount = 0;
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        if (devInfo[itrace].name.length > 0) 
        {
            devList[traceCount] = {name : devInfo[itrace].name};
            ++traceCount;
        }
    }
    if (traceCount < 1) return;
    var queryFilter = { $or: devList };
    sendWebSocketMessageObject(
        {
            topic       : 'getDeviceValues',
            userID      : userID,
            queryFilter : queryFilter
        });

}
function loadDeviceValues(data)
{
    var devices = data.devices;
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        if (devInfo[itrace].name.length > 0)
        {
            devInfo[itrace].device = devices.find(device => device.name === devInfo[itrace].name);
            var attr = getAttrObject(devInfo[itrace].device, itrace);
            var valueColor = 'black';
            if (attr.alarm.value > 0) valueColor = 'orange';
            if (attr.alarm.value > 2) valueColor = 'red';
            $('#valueSelect' + itrace.toString()).css("color", valueColor);
            $('#valueSelect' + itrace.toString()).html(attr.value.toString());
            $('#unitSelect' + itrace.toString()).css("color", valueColor);
            $('#unitSelect' + itrace.toString()).html(attr.unit);

        }
    }
    upDatePlot();
}
function getAttrObject(device, itrace)
{
    var props = device[devInfo[itrace].prop];
    var attr = props.find(attr => attr.attr === devInfo[itrace].attr);
    return attr;
}
function selectAxis(itrace,axis)
{
    devInfo[itrace].axis  = axis;
}
function selectPts(itrace)
{
    devInfo[itrace].pts = !devInfo[itrace].pts;
}
function startPlot()
{
    if (!plotStarted)
    {
        $("#startPlotButton").html('Stop Plot');
    }
    else
    {
        $("#startPlotButton").html('Start Plot');
    }
    plotStarted = !plotStarted;
    if (!plotStarted)
    {
        plotTraces = [];
        for (var itrace = 0; itrace < numTraces; ++itrace)
        {
            deviceSelect[itrace].disabled = false;
            propSelect[itrace].disabled = false;
            attrSelect[itrace].disabled = false;
            $('#plotAxis_' + itrace.toString() + 'y1').prop( "disabled", false );
            $('#plotAxis_' + itrace.toString() + 'y2').prop( "disabled", false );
            $('#pts_' + itrace.toString()).prop( "disabled", false );
        }
        return;
    }
    plotTraces = [];

    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        deviceSelect[itrace].disabled = true;
        propSelect[itrace].disabled = true;
        attrSelect[itrace].disabled = true;
        $('#plotAxis_' + itrace.toString() + 'y1').prop( "disabled", true );
        $('#plotAxis_' + itrace.toString() + 'y2').prop( "disabled", true );
        $('#pts_' + itrace.toString()).prop( "disabled", true );
        if (devInfo[itrace].name.length > 0)
        {
            var modeType = 'lines';
            if (devInfo[itrace].pts) modeType = 'markers';
            var trace =  
            {
                x: [],
                y: [],
                name: devInfo[itrace].axis + ' ' + devInfo[itrace].name + '.' + devInfo[itrace].prop + '.' + devInfo[itrace].attr + ' (' + getAttrObject(devInfo[itrace].device,itrace).unit + ')',
                yaxis: devInfo[itrace].axis,
                type: 'scatter',
                mode: modeType
            }
            plotTraces.push(trace);
        }
    }
    plotLayout =
    {
        showlegend: true,
        legend: {x: 0,y: 1.35},
        xaxis: {title: 'Time (seconds)'},
        yaxis: {title: 'y1'},
        yaxis2: {title: 'y2',overlaying: 'y',side: 'right'}
    };

    plotStartTime = new Date().getTime();
    Plotly.newPlot('timePlot', plotTraces, plotLayout);
    plotLength = Number($("#plotLength").val());
}
function upDatePlot()
{
    if (!plotStarted) return
    var now = (new Date().getTime() - plotStartTime) / 1000;
    var plotBeginning = now - plotLength;
    var traceCount = 0;
    var deltaT = (new Date().getTime() - plotStartTime) / 1000;
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        if (devInfo[itrace].name.length > 0)
        {
            var removeDone = false;
            while(!removeDone)
            {
                if (plotTraces[traceCount].x[0] < plotBeginning)
                {
                    plotTraces[traceCount].x.shift();
                    plotTraces[traceCount].y.shift();
                }
                else
                {
                    removeDone = true;
                }
            }
            plotTraces[traceCount].x.push(deltaT);
            plotTraces[traceCount].y.push(getAttrObject(devInfo[itrace].device,itrace).value);
            ++traceCount;
        }
    }
    Plotly.newPlot('timePlot', plotTraces, plotLayout);
}

