msg.payload.settings = msg.device.settings;
msg.payload.timeStamp = new Date().getTime();
msg.payload.arcPeriod = msg.device.arcPeriod;
var archiveMsg = null;
if ((msg.payload.timeStamp - msg.payload.lastArchive) > msg.payload.arcPeriod)
{
    msg.payload.lastArchive  = msg.payload.timeStamp;
    msg.device.lastArchive = msg.payload.lastArchive;
    msg.device.timeStamp = msg.payload.timeStamp;
    var archiveDevice = JSON.parse(JSON.stringify(msg.device));
    delete archiveDevice.arcPeriod;
    delete archiveDevice.lastArchive;
    for (var ii = 0; ii < archiveDevice.readings.length; ++ii)
    {
        if (archiveDevice.readings[ii].type == 'scalar')
        {
            delete archiveDevice.readings[ii].unit;
            delete archiveDevice.readings[ii].alarm;
        }
        delete archiveDevice.readings[ii].type;
    }
    for (var ii = 0; ii < archiveDevice.settings.length; ++ii)
    {
        if (archiveDevice.settings[ii].type == 'scalar')
        {
            delete archiveDevice.settings[ii].unit;
            delete archiveDevice.settings[ii].alarm;
        }
        delete archiveDevice.settings[ii].type;
    }
    archiveMsg = {topic:'archiveData',payload: archiveDevice};
}
var updateDeviceMsg = {topic:'updateDevice',payload:[{name:msg.payload.name},msg.payload]};
return [updateDeviceMsg,archiveMsg];

function isEmpty(obj) 
{
    for(var key in obj) 
    {
        if(obj.hasOwnProperty(key))
            return false;
    }
    return true;
}
if (isEmpty(msg.payload)) return null;
return msg;

var projectionFilter =  {projection:{_id:0}};
var newMsg = 
{
    topic           : 'getDeviceValues',
    userID          : msg.payload.userID,
    payload         : [msg.payload.queryFilter, projectionFilter],
};
return newMsg;

function objectLength( object ) {
    var length = 0;
    for( var key in object ) {
        if( object.hasOwnProperty(key) ) {
            ++length;
        }
    }
    return length;
}
var numDevices = objectLength(msg.payload);
if (numDevices < 1) return null;
var devices = [];
for (var idev = 0; idev < numDevices; ++idev) devices[idev] = msg.payload[idev];
return {
    topic:'getDeviceValues',
    payload:{
        topic           : 'getDeviceValues',
        userID          : msg.userID,
        devices         : devices,

    }
};

