            type: 'scatter',
            mode: modeType
        }
        for (var ipt = 0; ipt < archiveList[ii].trace.length; ++ipt)
        {
            trace.x[ipt] = (archiveList[ii].trace[ipt].timeStamp - startDate) / 3600000;
