var numTraces = 1;
var deviceSelect = [];
var propSelect=[];
var attrSelect=[];
var valSelect=[];
var devInfo = [];

var graph3d = null;
var itimeSlice = 0;
var timeMax = 32;
var dataArray = [];
var timeStamp = [];
var deltaT = 0;
var freeze = false;
var sliceViewActive = false;
var birdsEyeViewActive = false;
var topViewActive = false;
var birdsEyeTimeMax = 32;
var plotStarted = false;

var threedPlotOptions =
{
    width: '850px',
    height: '600px',
    style: 'surface',
    showPerspective: false,
    showGrid: true,
    showShadow: false,
    keepAspectRatio: false,
    verticalRatio: 0.6,
    showZAxis: true,
    xLabel: 'x',
    yLabel: 'Time',
    tooltip: true,
    axisColor: '#ffffff',
};

useWebSockets(true);
function onDocumentReady()
{
    $("#freezeButton").text('Running');
    $("#plotslices").val(timeMax.toString());
    $("#plotslices").change(function()
    {
        timeMax = Number($("#plotslices").val());
        if (timeMax < 2)
        {
            alert('Number of plot slices must be greater than 1');
            timeMax = 2;
        }
        birdsEyeTimeMax = timeMax;
        $("#plotslices").val(timeMax.toString());
        dataArray = [];
        itimeSlice = 0;
        plotStarted = false;
    });
    sliceViewActive = true;
    birdsEyeViewActive = false;
    topViewActive = false;

    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        deviceSelect[itrace] = document.getElementById('deviceSelect' + itrace.toString());
        propSelect[itrace] = document.getElementById('propSelect' + itrace.toString());
        attrSelect[itrace] = document.getElementById('attrSelect' + itrace.toString());
        devInfo[itrace] = {name : '', prop : '', attr : '',device : {}};
    }
    setInterval(getDeviceValues, 1000);
}
function onWebSocketOpen()
{
    sendWebSocketMessageObject({topic : 'getDeviceList',payload : {}, 'userID' : userID,});
}
function onWebSocketClose()
{

}
function onWebSocketMessage(msg)
{
    if (msg.userID == userID)
    {
        switch(msg.topic)
        {
            case 'getDeviceList':
                loadDeviceList(msg.payload);
                break;
            case 'getAttrList':
                loadAttrList(msg);
                break;
            case 'getDeviceValues':
                loadDeviceValues(msg);
                break;
            default:
            // code block
        }
    }
}

function loadDeviceList(deviceList)
{
    deviceList.sort();
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        for (var ii = 0; ii < deviceList.length; ++ii)
        {
            var opt = document.createElement('option');
            opt.value = deviceList[ii];
            opt.innerHTML = deviceList[ii];
            deviceSelect[itrace].appendChild(opt);
        }
        var readOpt = document.createElement('option');
        readOpt.value = 'readings';
        readOpt.innerHTML = 'readings';
        propSelect[itrace].appendChild(readOpt);
        var setOpt = document.createElement('option');
        setOpt.value = 'settings';
        setOpt.innerHTML = 'settings';
        propSelect[itrace].appendChild(setOpt);
    }
}
function deviceSelected(itrace)
{
    propSelect[itrace].value = 'notSelected';
    clearAttrOptionList(itrace);
}
function propSelected(itrace)
{
    if (propSelect[itrace].value == 'notSelected') return;
    if(deviceSelect[itrace].value == 'notSelected')
    {
        propSelect[itrace].value = 'notSelected';
        return;
    }
    clearAttrOptionList(itrace);
    sendWebSocketMessageObject(
        {
            topic       : 'getAttrList',
            userID      : userID,
            trace       : itrace,
            name        : deviceSelect[itrace].value,
            prop        : propSelect[itrace].value
        });
}
function loadAttrList(data)
{
    data.attrList.sort();
    for (var ii = 0; ii < data.attrList.length; ++ii)
    {
        var opt = document.createElement('option');
        opt.value = data.attrList[ii];
        opt.innerHTML = data.attrList[ii];
        attrSelect[data.trace].appendChild(opt);
    }
}
function attrSelected(itrace)
{
    devInfo[itrace].name = '';
    devInfo[itrace].prop = '';
    devInfo[itrace].attr = '';
    devInfo[itrace].device = {};
    if (attrSelect[itrace].value == 'notSelected') 
    {
        $('#valueSelect' + itrace.toString()).html('');
        $('#unitSelect' + itrace.toString()).html('');
        return;
    }
    devInfo[itrace].name = deviceSelect[itrace].value;
    devInfo[itrace].prop = propSelect[itrace].value;
    devInfo[itrace].attr = attrSelect[itrace].value;


}
function clearAttrOptionList(itrace)
{
    devInfo[itrace].name = '';
    devInfo[itrace].prop = '';
    devInfo[itrace].attr = '';
    devInfo[itrace].device = {};

    while (attrSelect[itrace].firstChild)
    {
        attrSelect[itrace].removeChild(attrSelect[itrace].firstChild);
    }
    var opt = document.createElement('option');
    opt.value = 'notSelected';
    opt.innerHTML = '';
    attrSelect[itrace].appendChild(opt);
    $('#valueSelect' + itrace.toString()).html('');
    $('#unitSelect' + itrace.toString()).html('');
}
function getDeviceValues()
{
    var devList = [];
    var traceCount = 0;
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        if (devInfo[itrace].name.length > 0) 
        {
            devList[traceCount] = {name : devInfo[itrace].name};
            ++traceCount;
        }
    }
    if (traceCount < 1) return;
    var queryFilter = { $or: devList };
    sendWebSocketMessageObject(
        {
            topic       : 'getDeviceValues',
            userID      : userID,
            queryFilter : queryFilter
        });

}
function loadDeviceValues(data)
{
    var devices = data.devices;
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        if (devInfo[itrace].name.length > 0)
        {
            devInfo[itrace].device = devices.find(device => device.name === devInfo[itrace].name);
            var attr = getAttrObject(devInfo[itrace].device, itrace);
        }
    }
    upDatePlot(attr.value);
}
function upDatePlot(value)
{
    var npts = value[0].length;
    if(!plotStarted)
    {
        dataArray = [];
        for (var ix = 0; ix < npts; ++ix)
        {
            dataArray[ix] =
            {
                'x': value[0][ix],
                'y': 0,
                'z': value[1][ix],
                'style': value[1][ix]
            };
        }
        var container = document.getElementById('spectrogram');

        graph3d = new vis.Graph3d(container, dataArray, threedPlotOptions);
        $("#startTime").text(new Date().toLocaleString());
        $("#stopTime").text(new Date().toLocaleString());
        graph3d.on('cameraPositionChange', onCameraPositionChange);
        if (sliceViewActive) sliceView();
        if (birdsEyeViewActive) birdsEyeView();
        if (topViewActive) topView();
        plotStarted = true;
    }
    else
    {
        if (freeze) return;
        if (itimeSlice < timeMax)
        {
            timeStamp[itimeSlice] = new Date().getTime();
            deltaT = (timeStamp[itimeSlice] - timeStamp[0]) / 1000;
            for (var ix = 0; ix < npts; ++ix)
            {
                dataArray[itimeSlice * npts + ix] =
                {
                    'x': value[0][ix],
                    'y': deltaT,
                    'z': value[1][ix],
                    'style': value[1][ix]
                };
            }
            ++itimeSlice;
        }
        else
        {
            for (var itime = 0; itime < (timeMax - 1); ++itime)
            {
                timeStamp[itime] = timeStamp[itime + 1];
                deltaT = (timeStamp[itime] - timeStamp[0]) / 1000;
                for (var ix = 0; ix < npts; ++ix)
                {
                    dataArray[itime * npts + ix] =
                    {
                        'x': dataArray[(itime + 1) * npts + ix].x,
                        'y': deltaT,
                        'z': dataArray[(itime + 1) * npts + ix].z,
                        'style': dataArray[(itime + 1) * npts + ix].style
                    };
                }
            }
            timeStamp[timeMax - 1] = new Date().getTime();
            deltaT = (timeStamp[timeMax - 1] - timeStamp[0]) / 1000;
            for (var ix = 0; ix < npts; ++ix)
            {
                dataArray[(timeMax - 1) * npts + ix] =
                {
                    'x': value[0][ix],
                    'y': deltaT,
                    'z': value[1][ix],
                    'style': value[1][ix]
                };
            }
        }
        $("#startTime").text(new Date(timeStamp[0]).toLocaleString());
        $("#stopTime").text(new Date(timeStamp[itimeSlice - 1]).toLocaleString());
        graph3d.setData(dataArray);
    }
}
function getAttrObject(device, itrace)
{
    var props = device[devInfo[itrace].prop];
    var attr = props.find(attr => attr.attr === devInfo[itrace].attr);
    return attr;
}
function freezeRun()
{
    freeze = !freeze;
    if (freeze)
    {
        $("#freezeButton").text('Frozen');
    }
    else
    {
        $("#freezeButton").text('Running');
    }
}

function birdsEyeView()
{
    var horzAngle = 315.0 * 3.1415927 / 180.0;
    var vertAngle = 45.0 * 3.1415927 / 180.0;
    graph3d.setCameraPosition({'horizontal': horzAngle, 'vertical': vertAngle, 'distance': 2.0});
    if (sliceViewActive)
    {
        timeMax =  birdsEyeTimeMax;
        $("#plotslices").val(timeMax.toString());
    }
    sliceViewActive = false;
    birdsEyeViewActive = true;
    topViewActive = false;
}
function sliceView()
{
    var horzAngle = 0.0 * 3.1415927 / 180.0;
    var vertAngle = 0.0 * 3.1415927 / 180.0;
    graph3d.setCameraPosition({'horizontal': horzAngle, 'vertical': vertAngle, 'distance': 1.1});
    timeMax = 2;
    $("#plotslices").val(timeMax.toString());
    itimeSlice = 0;
    plotStarted = false;
    sliceViewActive = true;
    birdsEyeViewActive = false;
    topViewActive = false;
}
function topView()
{
    var horzAngle = 0 * 3.1415927 / 180.0;
    var vertAngle = 90 * 3.1415927 / 180.0;
    graph3d.setCameraPosition({'horizontal': horzAngle, 'vertical': vertAngle, 'distance': 2.0});
    if (sliceViewActive)
    {
        timeMax =  birdsEyeTimeMax;
        $("#plotslices").val(timeMax.toString());
    }
    sliceViewActive = false;
    birdsEyeViewActive = false;
    topViewActive = true;
}
function onCameraPositionChange(event)
{
    if (sliceViewActive) sliceView();
    if (birdsEyeViewActive) birdsEyeView();
    if (topViewActive) topView();
}

