// GaugeWidget
        function setGaugeWidget(gaugeConfig)
        {
            var degrees = 180.0 * (gaugeConfig.hihi - gaugeConfig.value) / (gaugeConfig.hihi - gaugeConfig.lolo);
            if (degrees < 0.0 )   degrees = 0.0;
            if (degrees > 180.0 ) degrees = 180.0;
            // Trig to calc meter point
            var radius = 0.50;
            var radians = degrees * Math.PI / 180;
            var x = radius * Math.cos(radians);
            var y = radius * Math.sin(radians);
            var path1 = (degrees < 45 || degrees > 135) ? 'M -0.0 -0.025 L 0.0 0.025 L ' : 'M -0.025 -0.0 L 0.025 0.0 L ';
            // Path: may have to change to create a better triangle
            var mainPath = path1,
                pathX = String(x),
                space = ' ',
                pathY = String(y),
                pathEnd = ' Z';
            var rotation = (180.0 * (gaugeConfig.high - gaugeConfig.lolo) / (gaugeConfig.hihi - gaugeConfig.lolo)) - 90.0;
            var path = mainPath.concat(pathX,space,pathY,pathEnd);

            var valueColor = gaugeConfig.backgroundColor; 

            if ( (gaugeConfig.lolo <= gaugeConfig.value) && (gaugeConfig.value < gaugeConfig.low) )
            {
                valueColor = gaugeConfig.lowColor;
            }
            if ( (gaugeConfig.low  <= gaugeConfig.value) && (gaugeConfig.value < gaugeConfig.high) )
            {
                valueColor = gaugeConfig.mediumColor;
            }
            if ( (gaugeConfig.high <= gaugeConfig.value) && (gaugeConfig.value < gaugeConfig.hihi) )
            {
              valueColor = gaugeConfig.highColor;
            }
            if ( gaugeConfig.hihi <= gaugeConfig.value)
            {
                valueColor = gaugeConfig.highColor;
            }


            var data = 
                [
                    { 
                        type: 'scatter',
                        x: [0], y:[0],
                        marker: {size: 14, color:valueColor},
                        showlegend: false,
                        name: gaugeConfig.label,
                        text: gaugeConfig.value.toString(),
                        hoverinfo: 'text+name',


                    },
                    { 
                        values: 
                            [
                                gaugeConfig.hihi - gaugeConfig.high, 
                                gaugeConfig.high - gaugeConfig.low, 
                                gaugeConfig.low  - gaugeConfig.lolo, 
                                gaugeConfig.hihi - gaugeConfig.lolo 
                            ],
                        rotation: rotation,
                        text: 
                            [
                                gaugeConfig.high.toString() + '-' + gaugeConfig.hihi.toString(), 
                                gaugeConfig.low.toString()  + '-' + gaugeConfig.high.toString(), 
                                gaugeConfig.lolo.toString() + '-' + gaugeConfig.low.toString(), 
                                ''
                            ],
                        textinfo: 'text',
                        textposition:'inside',
                        textfont: {color: gaugeConfig.labelColor},
                        marker: 
                            {
                                colors:
                                    [
                                        gaugeConfig.highColor,
                                        gaugeConfig.mediumColor,
                                        gaugeConfig.lowColor, 
                                        gaugeConfig.backgroundColor
                                    ]
                            },
                        hoverinfo: 'label',
                        hole: 0.5,
                        type: 'pie',
                        showlegend: false,
                        sort: false
                    }
                ];
            var layout = 
                {
                    shapes:
                        [
                            {
                                type: 'path',
                                path: path,
                                fillcolor: valueColor,
                                line: {color: valueColor }
                            }
                        ],
                    height: gaugeConfig.size,
                    width: gaugeConfig.size,
                    title: 
                        {
                            text:gaugeConfig.label + ': ' + gaugeConfig.value.toString() + gaugeConfig.unit,
                            font: {color: valueColor},
                         },
                    xaxis: 
                        {
                            zeroline:false, 
                            showticklabels:false,
                            showgrid: false, 
                            range: [-1, 1],
                        },
                    yaxis: 
                        {
                            zeroline:false, 
                            showticklabels:false,
                            showgrid: false, 
                            range: [-1, 1]
                        },
                    margin: 
                        {
                            t: 40, //top margin
                            l: 0, //left margin
                            r: 0, //right margin
                            b: 20 //bottom margin
                        },
                    plot_bgcolor:gaugeConfig.backgroundColor,
                    paper_bgcolor:gaugeConfig.backgroundColor

                };
            $('#' + gaugeConfig.htmlId).attr('style','max-height: calc(' + (gaugeConfig.size / 2).toString() + 'px + 20px) !important; overflow:hidden;');
            Plotly.newPlot(gaugeConfig.htmlId, data, layout, {'displayModeBar': false});
        }

// StripChart Widget
        class StripChart
        {
           constructor() 
            {
                this.plotStartTime = new Date().getTime();
                this.plotLength = 600;
                this.htmlId = 'ampPowStripChart';
                this.layout =
                {
                    showlegend  : true,
                    legend      : { x: 0, y: 1.15},
                    height      : '500',
                    width       : '',
                    xaxis       :
                    {
                        title: 'Seconds since ' + new Date(this.plotStartTime).toLocaleString(),
                    },
                   yaxis:
                    {
                        title           : plotConfig.trace1Title,
                        titlefont       : {color: plotConfig.trace1Color},
                        tickfont        : {color: plotConfig.trace1Color},
                        gridcolor       : plotConfig.trace1GridColor,
                        zerolinecolor   : plotConfig.trace1GridColor,
                        linecolor       : plotConfig.trace1GridColor,

                    },
                    yaxis2:
                    {
                        title           : plotConfig.trace2Title,
                        titlefont       : {color: plotConfig.trace2Color},
                        tickfont        : {color: plotConfig.trace2Color},
                        gridcolor       : plotConfig.trace2GridColor,
                        zerolinecolor   : plotConfig.trace2GridColor,
                        linecolor       : plotConfig.trace2GridColor,
                        overlaying: 'y',
                        side: 'right',
//                        range: [0,10]
                    },
                    margin: 
                        {
                            t: 30, //top margin
        //                    l: 0, //left margin
        //                    r: 0, //right margin
        //                    b: 20 //bottom margin
                        },
                };
                this.trace1 = 
                {
                    x: [],
                    y: [],
                    name: plotConfig.trace1Title,
                    yaxis: 'y1',
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: plotConfig.trace1Color}

                };
                this.trace2 = 
                {
                    x: [],
                    y: [],
                    name: plotConfig.trace2Title,
                    yaxis: 'y2',
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: plotConfig.trace2Color}
                };
            }
            addTracePoints(trace1Pt, trace2Pt)
            {
                var now = (new Date().getTime() - this.plotStartTime) / 1000;
                var plotBeginning = now - this.plotLength;
                var removeDone = false;
                while(!removeDone)
                {
                    if (this.trace1.x[0] < plotBeginning)
                    {
                        this.trace1.x.shift();
                        this.trace1.y.shift();
                        this.trace2.x.shift();
                        this.trace2.y.shift();
                    }
                    else
                    {
                        removeDone = true;
                    }
                }

                this.trace1.x.push(now);
                this.trace2.x.push(now);
                this.trace1.y.push(trace1Pt);
                this.trace2.y.push(trace2Pt);
                Plotly.newPlot(this.htmlId, [this.trace1, this.trace2], this.layout);
            }
        }

