// spectrogram Javascript
        function initializeDataArray()
        {   
            oodataArray = [];
            itimeSlice = 0;
            for (var imode = modeMin; imode <= modeMax; ++imode)
            {
                var value = 0.0;
                oodataArray[imode - modeMin] =
                {
                    'x': imode,
                    'y': 0,
                    'z': value,
                    'style': value
                };
            }
        }
        function drawSpectrogram()
        {
            var container = document.getElementById('spectrogram');
            initializeDataArray();

            graph3d = new vis.Graph3d(container, oodataArray, threedPlotOptions);
            $("#startTime").text(new Date().toLocaleString());
            $("#stopTime").text(new Date().toLocaleString());
            graph3d.on('cameraPositionChange', onCameraPositionChange);
            birdsEyeView();
        }
        function addOoDataArrays()
        {
            var spectrum =  getAttrObject('readings', 'spectrum');
            var maxAmp =  getAttrObject('readings', 'maxAmp');
            var maxAmpMode =  getAttrObject('readings', 'maxAmpMode');
            var minDisplayMode =  getAttrObject('settings', 'minDisplayMode');
            var maxDisplayMode =  getAttrObject('settings', 'maxDisplayMode');
            var spectrumArray = spectrum.value[1];
            if ((modeMax != maxDisplayMode.value) || (modeMin != minDisplayMode.value))
            {
                modeMin = minDisplayMode.value;
                modeMax = maxDisplayMode.value;
                initializeDataArray();
                graph3d.redraw();
                return;
            }
            if (freeze) return;

            var mostExcitedModeAmp = maxAmp.value;
            var mostExcitedMode = maxAmpMode.value;
            $("#maxMode").text(Math.round(100 * mostExcitedMode) / 100);
            $("#maxModeAmplitude").text(Math.round(100 * mostExcitedModeAmp) / 100);

            if (itimeSlice < timeMax)
            {
                timeStamp[itimeSlice] = new Date().getTime();
                deltaT = (timeStamp[itimeSlice] - timeStamp[0]) / 1000;
                for (var imode = modeMin; imode <= modeMax; ++imode)
                {
                    var value = Math.round(100.0 * Number(spectrumArray[imode])) / 100.0;
                    oodataArray[itimeSlice * (modeMax - modeMin + 1) + (imode - modeMin)] =
                    {
                        'x': imode,
                        'y': deltaT,
                        'z': value,
//                        'style': value
                    };
                }
                ++itimeSlice;
            }
            else
            {
                for (var itime = 0; itime < (timeMax - 1); ++itime)
                {
                    timeStamp[itime] = timeStamp[itime + 1];
                    deltaT = (timeStamp[itime] - timeStamp[0]) / 1000;
                    for (var imode = modeMin; imode <= modeMax; ++imode)
                    {
                        oodataArray[itime * (modeMax - modeMin + 1) + (imode - modeMin)] =
                        {
                            'x': oodataArray[(itime + 1) * (modeMax - modeMin + 1) + (imode - modeMin)].x,
                            'y': deltaT,
                            'z': oodataArray[(itime + 1) * (modeMax - modeMin + 1) + (imode - modeMin)].z,
//                            'style': oodataArray[(itime + 1) * (modeMax - modeMin + 1) + (imode - modeMin)].style
                        };
                    }
                }
                timeStamp[timeMax - 1] = new Date().getTime();
                deltaT = (timeStamp[timeMax - 1] - timeStamp[0]) / 1000;
                for (var imode = modeMin; imode <= modeMax; ++imode)
                {
                    var value = Math.round(100.0 * Number(spectrumArray[imode])) / 100.0;
                    oodataArray[(timeMax - 1) * (modeMax - modeMin + 1) + (imode - modeMin)] =
                    {
                        'x': imode,
                        'y': deltaT,
                        'z': value,
//                        'style': value
                    };
                }
            }
            $("#startTime").text(new Date(timeStamp[0]).toLocaleString());
            $("#stopTime").text(new Date(timeStamp[itimeSlice - 1]).toLocaleString());
            graph3d.setData(oodataArray);
        }
