var mqttArray = msg.topic.split('/');
var mqtt = mqttArray[0];
for (var ii = 1; ii < (mqttArray.length - 1); ++ii) mqtt = mqtt + '/' + mqttArray[ii]; 
return {topic : msg.topic, payload:{topic:'readings', mqtt : mqtt, payload:msg.payload}};

var projectionFilter =  
{
    projection:
    {
        _id                         :   0, 
        name                        :   1,  
        timeStamp                   :   1, 
        [msg.payload.device.prop]   : 
        {
            $elemMatch  : 
            {   
                attr    : msg.payload.device.attr 
            }
        } 
    } 
};
var queryFilter = 
{
    $and :
    [
        {
            name: msg.payload.device.name
        },
        {
            timeStamp :
            {
                $gte: msg.payload.device.startDate,
                $lte: msg.payload.device.stopDate
            }
        }
    ] 

}
var newMsg = 
{
    topic           : 'getArchiveValues',
    userID          : msg.payload.userID,
    payload         : [queryFilter, projectionFilter],
    device          : msg.payload.device,
};
return newMsg;

function objectLength( object ) {
    var length = 0;
    for( var key in object ) {
        if( object.hasOwnProperty(key) ) {
            ++length;
        }
    }
    return length;
}

function sortByKey(arr, key) 
{
    return arr.sort( function(a, b) 
        {
            var x = a[key]; 
            var y = b[key];
            return ((x < y) ? -1 : ((x > y) ? 1 : 0));
        } );
}

var numRecords = objectLength(msg.payload);
if (numRecords < 1)
{
    return {
        topic : msg.device.name,
        payload:{
            topic           : 'getArchiveValues',
            payload         : {device: msg.device, trace:null},
            userID          : msg.userID,
        }
    };
}
var records = [];
for (var ii = 0; ii < numRecords; ++ii) records[ii] = msg.payload[ii]; 
sortByKey(records, 'timeStamp');
var trace = [];
for (var ii = 0; ii < numRecords; ++ii)
{
    trace[ii] = {timeStamp: records[ii].timeStamp, value: records[ii][msg.device.prop][0].value};
}

return {
    topic : msg.device.socket,
    payload:{
        topic           : 'getArchiveValues',
        payload         : {device: msg.device, trace: trace},
        userID          : msg.userID,
    }
};

return {
    topic   : msg.payload.mqttMessage.topic, 
    payload : msg.payload.mqttMessage.payload, 
    token   : msg.payload.token, 
    userID  : msg.payload.userID };

if (msg.payload.payload.loginPassword != global.get('settingsPassword'))
{
    return [{topic:'getJwtToken', payload:{topic:'getJwtToken', userID:msg.payload.userID, token: null}}, null];
}
return [null, {topic:'getJwtToken',payload:msg.payload.userID, userID : msg.payload.userID}];

if (Number(msg.token) == msg.userID)
{
    return [msg, {topic : 'acknowledge', payload:{topic:'settingAcknowledge', payload: 'Success!', userID : msg.userID}}]
}
return [null, {topic : 'acknowledge', payload:{topic:'settingAcknowledge', payload: 'Bad token', userID : msg.userID}}];

