template:
// Global Javascript
        var deviceLeader= {{{payload.script.deviceLeader}}};
        useWebSockets(true);
        var motorSpeedSliderMoved = false;
        function onDocumentReady()
        {
            $( function() 
            {
                $( "#motorSpeedSlider" ).slider(
                    {
                        stop    :   function( event, ui ) 
                                    {
                                        $('#motorSpeed').hide();
                                        var motorSpeedValue = Math.round(1.2 * ui.value) + 100;
                                        var motorSpeedSetting = getAttrObject( 'settings', 'motor-speed');
                                        motorSpeedSetting.value = motorSpeedValue;
                                        var mqttMessage = 
                                        {
                                            topic   : mqtt + '/setting',
                                            payload :  device
                                        };
                                        checkLogin(sendSettingToMqtt, mqttMessage);

                                        setTimeout(function()
                                        {
                                            motorSpeedSliderMoved = false; 
                                            $('#motorSpeed').show();
                                        }, 1500);

                                    },
                        start   :   function( event, ui ){motorSpeedSliderMoved = true},
                    });
            } );
            $("#motorSpeedSlider").children("div").css("background","red");
        }
        function onWebSocketOpen()
        {
        }
        function onWebSocketClose()
        {

        }
        function onWebSocketMessage(msg)
        {
            switch(msg.topic)
            {
                case 'readings':
                    heartbeat();
                    device = msg.payload;
                    mqtt = msg.mqtt;
                    $('#linkQualityId').text(getAttrObject('readings', 'linkQuality').value);
                    $('#signalLevelId').text(getAttrObject('readings', 'signalLevel').value);
                    if (!motorSpeedSliderMoved)
                    {   
                        var motorSpeed = getAttrObject('settings', 'motor-speed').value;
                        $('#motorSpeed').text(motorSpeed);
                        $('#motorSpeedSlider' ).slider( "value", Math.round((motorSpeed - 100) / 1.2) );
                    }
                    var motorOn = getAttrObject('settings', 'motor-on').value;
                    if (motorOn == 0)
                    {
                        $('#motorOffButton').css('color', 'var(--color1)'); 
                        $('#motorOffButton').css('background-color', 'var(--color5)'); 
                        $('#motorOnButton').css('color', 'var(--color5)'); 
                        $('#motorOnButton').css('background-color', 'var(--color1)'); 
                    }
                    else
                    {
                        $('#motorOffButton').css('color', 'var(--color5)'); 
                        $('#motorOffButton').css('background-color', 'var(--color1)'); 
                        $('#motorOnButton').css('color', 'var(--color1)'); 
                        $('#motorOnButton').css('background-color', 'var(--color5)'); 
                    }
                    var motorDirection = getAttrObject('settings', 'motor-direction').value;
                    if (motorDirection == 0)
                    {
                        $('#motorDirectionCCWButton').css('color', 'var(--color1)'); 
                        $('#motorDirectionCCWButton').css('background-color', 'var(--color5)'); 
                        $('#motorDirectionCWButton').css('color', 'var(--color5)'); 
                        $('#motorDirectionCWButton').css('background-color', 'var(--color1)'); 
                    }
                    else
                    {
                        $('#motorDirectionCCWButton').css('color', 'var(--color5)'); 
                        $('#motorDirectionCCWButton').css('background-color', 'var(--color1)'); 
                        $('#motorDirectionCWButton').css('color', 'var(--color1)'); 
                        $('#motorDirectionCWButton').css('background-color', 'var(--color5)'); 
                    }

                    break;
                case 'getJwtToken':
                    if (msg.userID == userID) loadJwtToken(msg);
                    break;
                case 'settingAcknowledge':
                    if (msg.userID == userID)
                    {
                        if (msg.payload != 'Success!') acknowledgeDialog('Acknowledge', 'Setting', msg.payload);
                    }
                    break;
                default:
                // code block
            }
        }

        function getAttrObject(propsName, attrName)
        {
            var props = device[propsName];
            var attr = props.find(attr => attr.attr === attrName);
            return attr;
        }
        function setMotorOn(motorOn)
        {
            var motorOnSetting = getAttrObject( 'settings', 'motor-on');
            motorOnSetting.value = motorOn;
            var mqttMessage = 
            {
                topic   : mqtt + '/setting',
                payload :  device
            };
            checkLogin(sendSettingToMqtt, mqttMessage);
        }
        function setMotorDirection(motorDirection)
        {
            var motorDirectionSetting = getAttrObject( 'settings', 'motor-direction');
            motorDirectionSetting.value = motorDirection;
            var mqttMessage = 
            {
                topic   : mqtt + '/setting',
                payload :  device
            };
            checkLogin(sendSettingToMqtt, mqttMessage);
        }
