return {topic:msg.topic, payload:device, 'device':device, 'data':data};

//console.log(msg);
var newMsg = {topic:msg.topic, payload:{}, query:msg.device};
if (msg.payload[0].type == 'scalar')
{
    msg.data.alarm = 0;
    if ( !isNaN(msg.data.value) && (msg.payload[0].lolo < msg.payload[0].low) && (msg.payload[0].low  < msg.payload[0].high) && (msg.payload[0].high  < msg.payload[0].hihi) )
    {
       if (msg.data.value < msg.payload[0].low)  msg.data.alarm = 1;
       if (msg.data.value < msg.payload[0].lolo) msg.data.alarm = 3;
       if (msg.data.value > msg.payload[0].high) msg.data.alarm = 2;
       if (msg.data.value > msg.payload[0].hihi) msg.data.alarm = 4;
    }
}
var archiveDeltat = msg.data.time - msg.payload[0].lastArchive;
var archiveMsg = null;
if (archiveDeltat > msg.payload[0].archive_period)
{
    msg.data.lastArchive = msg.data.time;
    archiveMsg = 
    {
        topic:'archiveData',
        payload:
        {
            device_id   :   msg.payload[0]._id, 
            type        :   msg.data.type,
            value       :   msg.data.value,
            time        :   msg.data.time
        }
    };
}
newMsg.payload = {$set:msg.data};
return [newMsg,archiveMsg];

