var ws;
var retries = 0;
var userID = -1;
var sysSelect = [];
var sysName = ['sys01','sys02','sys03','sys04','device','attr','prop'];
var numSys = sysName.length;
var lastSystemSelected = -1;
var deviceData = null;
var validDevice = false;
var databaseDumpCsvFile = null;
var newDevRecord = {};

$( document ).ready(function()
{
    userID  = getRandomInt(4096);
    wsConnectC();
    sysSelect = [];
    for (var isys = 0; isys < numSys; ++isys)
    {
        sysSelect[isys] = document.getElementById('sysSelect_'+ isys.toString());
    }
    $('#mqttTopicData').hide();
    $('#editDeviceButton').hide();
    $('#databaseDumpLink').hide();
});
$( function() 
{
    $( "#editDeviceDialog" ).dialog(
        {
            width:    500,
            autoOpen: false,
            buttons:
            [
                {
                    text: "Update",
                    click: function() {updateDatabaseWithDevInfo(); $( this ).dialog( "close" );},
                    showText: false
                },
