                    showText: false
                }

            ]
        });
} );
function wsConnectC()
{
    var uri = window.location.href.split('://');
    var wslead = 'ws://';
    if (uri[0] == 'https') wslead = 'wss://';
    ws = new WebSocket(wslead + uri[1] + '/websocket');
    ws.onmessage = function(event)
    {
        var msg = JSON.parse(event.data);
        if (msg.userID == userID)
        {
            switch(msg.topic)
            {
                case 'loadSystem':
                    loadSystem(msg);
                    break;
                case 'putDevInfo':
                    putDevInfo(msg);
                    break;
                case 'putDatabaseDump':
                    putDatabaseDump(msg);
                    break;
                case 'deviceExistCheck':
                    deviceExistCheck(msg);
                    break;
                case 'deviceCreated':
                    deviceCreated(msg);
                    break;
                case 'deviceDeleted' :
                    deviceDeleted(msg);
                    break;
                case 'deviceUpdated' :
                    deviceUpdated(msg);
                    break;
                default:
                // code block
            }
        }

    };
    ws.onopen = function()
    {
        console.log("Websocket connected");
        ws.send(JSON.stringify(
        {
            topic       : 'getDevSystem',
            system      : sysName[0],
            payload     : {},
            'userID'    : userID,
            sysSelectIndex  : 0,
        }));
    };
    ws.onclose = function()
    {
    };
}
function getRandomInt(max)
{
  return Math.floor(Math.random() * Math.floor(max));
}
function loadSystem(systemData)
{
    clearDownStreamColumns(systemData.sysSelectIndex);
    for (var ii = 0; ii < systemData.payload.length; ++ii)
    {
        opt = document.createElement('option');
        opt.value = systemData.payload[ii];
        opt.innerHTML = systemData.payload[ii];
        sysSelect[systemData.sysSelectIndex].appendChild(opt);
    }
}
function clearDownStreamColumns(sysSelectIndex)
{
    lastSystemSelected = sysSelectIndex;
    for (var isys = sysSelectIndex; isys < numSys; ++isys)
    {
        while (sysSelect[isys].firstChild)
        {
            sysSelect[isys].removeChild(sysSelect[isys].firstChild);
        }
    }
    var opt = document.createElement('option');
    opt.value = 'notSelected';
    opt.innerHTML = '';
    sysSelect[sysSelectIndex].appendChild(opt);
    validDevice = false;
    $('#editDeviceButton').hide();
    $('#mqttTopicData').hide();
    $( "#editDeviceDialog" ).dialog('close');
}
function sysSelected(sysSelectIndex)
{
    if (sysSelect[sysSelectIndex].value == 'notSelected') return;
    if(sysSelect[sysSelectIndex].firstChild.value == 'notSelected')
    {
        sysSelect[sysSelectIndex].removeChild(sysSelect[sysSelectIndex].firstChild);
    }
    if (sysSelectIndex < (numSys - 1))
    {
        var newMsg =
            {
                topic           : 'getDevSystem',
                system          : sysName[sysSelectIndex + 1],
                payload         : {},
                userID          : userID,
                sysSelectIndex  : sysSelectIndex + 1
            };
        for (var isys = 0; isys <= sysSelectIndex; ++isys)
        {
            newMsg.payload[sysName[isys]] = sysSelect[isys].value;
        }
        ws.send(JSON.stringify(newMsg));
    }
    else
    {
        validDevice = true;
        $('#editDeviceButton').show();
        getDevInfo();
    }
}
function getDevName()
{
    if (!validDevice) return;
    var devName = sysSelect[0].value;
    for (var isys = 1; isys < numSys; ++isys)
    {
        devName = devName + '-' + sysSelect[isys].value;
    }
    return devName;
}
function getDevInfo()
{
    if (!validDevice) return;
    var newMsg =
        {
            topic             : 'getDevInfo',
            payload           : {},
            userID            : userID,
        };
    for (var isys = 0; isys < numSys; ++isys)
    {
        newMsg.payload[sysName[isys]] = sysSelect[isys].value;
    }
    ws.send(JSON.stringify(newMsg));

}
function putDevInfo(msg)
{
    deviceData = msg;
    $( "#loloAlarm" ).val(deviceData.payload.lolo);
    $( "#hihiAlarm" ).val(deviceData.payload.hihi);
    $( "#lowAlarm" ).val(deviceData.payload.low);
    $( "#highAlarm" ).val(deviceData.payload.high);
    $( "#deviceUnit" ).val(deviceData.payload.unit);
    $( "#archPeriod" ).val(deviceData.payload.archive_period);
    var devTopic = 'archiver';
    for (var isys = 0; isys < numSys; ++isys)
    {
        devTopic = devTopic + '/' + sysSelect[isys].value;
    }
    var mqttScalar = {type:'scalar', value:0};
    var mqttVector = {type:'vector', value:[[1,2,3], [0.0, -1.0, 2.0]]};
    $('#mqttTopic').html(devTopic);
    if (deviceData.payload.type == 'scalar') $('#mqttPayloadStruct').html(JSON.stringify(mqttScalar));
    if (deviceData.payload.type == 'vector') $('#mqttPayloadStruct').html(JSON.stringify(mqttVector));
    $('#mqttTopicData').show();
}
function editDevice()
{
    $( "#editDeviceDialog" ).dialog( "option", "title", "Edit " + getDevName() );    
    $( "#editDeviceDialog" ).dialog( "open" );
}
function updateDatabaseWithDevInfo()
{
    var newMsg = 
    {
        topic           : 'updateDatabaseWithDevInfo',
        userID          : deviceData.userID,
        query           : { _id     : deviceData.payload._id },
        payload     :
        {
            '$set'  :
            { 
                'unit'              :   $( "#deviceUnit" ).val(),
                'lolo'              :   Number($( "#loloAlarm" ).val()),
                'hihi'              :   Number($( "#hihiAlarm" ).val()),
                'low'               :   Number($( "#lowAlarm" ).val()),
                'high'              :   Number($( "#highAlarm" ).val()),
                'archive_period'    :   Number($( "#archPeriod" ).val())
            }
        }
    };
    ws.send(JSON.stringify(newMsg));
}
function deviceUpdated(msg)
{
    $('#databaseDumpLink').hide();
    acknowledgeDialog('Acknowledge', 'Info', 'Device updated');
}
function getDatabaseDump()
{
    var newMsg =
        {
            topic             : 'getDatabaseDump',
            payload           : {},
            userID            : userID,
        };
    ws.send(JSON.stringify(newMsg));

}
function putDatabaseDump(msg)
{
    var databaseDump = '_id,sys01,sys02,sys03,sys04,device,attr,prop,type,archive_period,lolo,low,high,hihi,alarm,value,unit,time, ,lastArchive\n';
    for (var irec = 0; irec < msg.payload.length; ++irec)
    {
        var value = 'vector';
        if (msg.payload[irec].type == 'scalar') value = msg.payload[irec].value;
        databaseDump = databaseDump 
            + msg.payload[irec]._id + ',' 
            + msg.payload[irec].sys01 + ',' 
            + msg.payload[irec].sys02 + ',' 
            + msg.payload[irec].sys03 + ',' 
            + msg.payload[irec].sys04 + ',' 
            + msg.payload[irec].device + ',' 
            + msg.payload[irec].attr + ',' 
            + msg.payload[irec].prop + ',' 
            + msg.payload[irec].type + ',' 
            + msg.payload[irec].archive_period + ',' 
            + msg.payload[irec].lolo + ',' 
            + msg.payload[irec].low + ',' 
            + msg.payload[irec].high + ',' 
            + msg.payload[irec].hihi + ',' 
            + msg.payload[irec].alarm + ',' 
            + value + ',' 
            + msg.payload[irec].unit + ','
            + new Date(msg.payload[irec].time).toLocaleString() + ','
            + new Date(msg.payload[irec].lastArchive).toLocaleString()
            + '\n'; 
    }
    var dataBlob = new Blob([databaseDump], {type: 'text/plain'});

    // If we are replacing a previously generated file we need to
    // manually revoke the object URL to avoid memory leaks.
    if (databaseDumpCsvFile !== null) {
      window.URL.revokeObjectURL(databaseDumpCsvFile);
    }

    databaseDumpCsvFile = window.URL.createObjectURL(dataBlob);
    $('#databaseDumpLink').attr("href", databaseDumpCsvFile);
    $('#databaseDumpLink').attr("download", 'databaseDump.csv');
    $('#databaseDumpLink').show();
}
function createDevice()
{
    newDevRecord = {};
    newDevRecord = {
       sys01 :  $('#sys01InputField').val().replace(/\s/g,''),
       sys02 :  $('#sys02InputField').val().replace(/\s/g,''),
       sys03 :  $('#sys03InputField').val().replace(/\s/g,''),
       sys04 :  $('#sys04InputField').val().replace(/\s/g,''),
       device :  $('#deviceInputField').val().replace(/\s/g,''),
       attr :  $('#attrInputField').val().replace(/\s/g,''),
       prop :  $('#propInputSelect').val().replace(/\s/g,''),
       type :  $('#typeInputSelect').val().replace(/\s/g,''),
       archive_period: 60000,
       lolo: 1,
       low:1,
       high:-1,
       hihi:-1,
       alarm:0,
       unit:'unit',
       time: new Date().getTime(),
       lastArchive: 0
    }
    var validDeviceRecord = true;
    if (newDevRecord.sys01.length < 1) validDeviceRecord = false;
    if (newDevRecord.sys02.length < 1) validDeviceRecord = false;
    if (newDevRecord.sys03.length < 1) validDeviceRecord = false;
    if (newDevRecord.sys04.length < 1) validDeviceRecord = false;
    if (newDevRecord.device.length < 1) validDeviceRecord = false;
    if (newDevRecord.attr.length < 1) validDeviceRecord = false;
    if (newDevRecord.prop.length < 1) validDeviceRecord = false;
    if (newDevRecord.type.length < 1) validDeviceRecord = false;
    if (!validDeviceRecord)
    {
        acknowledgeDialog('Acknowledge', 'Warning!', 'Cannot have an empty field.');
        return;
    }
    var valy = 0;
    if (newDevRecord.type == 'vector') valy = [[],[]];
    newDevRecord.value = valy;
    var newMsg =
    {
        topic             : 'checkIfDeviceExists',
        payload           : 
        {
            sys01 :  newDevRecord.sys01,
            sys02 :  newDevRecord.sys02,
            sys03 :  newDevRecord.sys03,
            sys04 :  newDevRecord.sys04,
            device : newDevRecord.device,
            attr :   newDevRecord.attr,
            prop :   newDevRecord.prop,
        },
        userID            : userID,
    };
    ws.send(JSON.stringify(newMsg));
}
function deviceExistCheck(msg)
{
    if (msg.payload.length > 0) 
    {
        acknowledgeDialog('Acknowledge', 'Warning!', 'Device already exists');
        return;
    }
    var newMsg =
    {
        topic             : 'createDevice',
        userID            : userID,
        payload           : {}
    };
    newMsg.payload = newDevRecord;
    ws.send(JSON.stringify(newMsg));
}
function deviceCreated(msg)
{
    clearDownStreamColumns(0);
    ws.send(JSON.stringify(
    {
        topic       : 'getDevSystem',
        system      : sysName[0],
        payload     : {},
