// Socket Connect JavaScript
        function wsConnectC()
        {
            var uri = window.location.href.split('://');
            var wslead = 'ws://';
            if (uri[0] == 'https') wslead = 'wss://';
            ws = new WebSocket(wslead + uri[1] + '/websocket');
            ws.onmessage = function(event)
            {
                var msg = JSON.parse(event.data);
                switch(msg.topic)
                {
                    case 'readings':
                        setMaxAmpBarChart(msg.payload.maxAmp);
                        setAvgPowerBarChart(msg.payload.avgPower);
                        updateAmpPowStripChart(msg.payload);
                        addOoDataArrays(msg.payload);
                        break;
                    default:
                    // code block
                }

            };
            ws.onopen = function()
            {
                console.log("Websocket connected");
            };
            ws.onclose = function()
            {
                console.log("Websocket disconnected");
            };
        }

