// spectrogram Javascript
        function initializeDataArray()
        {   
            oodataArray = [];
            itimeSlice = 0;
            for (var imode = modeMin; imode <= modeMax; ++imode)
            {
                var value = 0.0;
                oodataArray[imode - modeMin] =
                {
                    'x': imode,
                    'y': 0,
                    'z': value,
                    'style': value
                };
            }
        }
        function drawSpectrogram()
        {
            var container = document.getElementById('spectrogram');
            initializeDataArray();

            graph3d = new vis.Graph3d(container, oodataArray, threedPlotOptions);
            $("#startTime").text(new Date().toLocaleString());
            $("#stopTime").text(new Date().toLocaleString());
            graph3d.on('cameraPositionChange', onCameraPositionChange);
            birdsEyeView();
        }
        function addOoDataArrays(data)
        {
            var spectrumArray = data.spectrum.value[1];
            if ((modeMax != data.max.value) || (modeMin != data.min.value))
            {
                modeMin = data.min.value;
                modeMax = data.max.value;
                initializeDataArray();
                graph3d.redraw();
                return;
            }
            if (freeze) return;

            var mostExcitedModeAmp = data.maxAmp.value;
            var mostExcitedMode = data.maxMode.value;
            $("#maxMode").text(Math.round(100 * mostExcitedMode) / 100);
            $("#maxModeAmplitude").text(Math.round(100 * mostExcitedModeAmp) / 100);

            if (itimeSlice < timeMax)
            {
                timeStamp[itimeSlice] = new Date().getTime();
                deltaT = (timeStamp[itimeSlice] - timeStamp[0]) / 1000;
                for (var imode = modeMin; imode <= modeMax; ++imode)
                {
                    var value = Math.round(100.0 * Number(spectrumArray[imode])) / 100.0;
                    oodataArray[itimeSlice * (modeMax - modeMin + 1) + (imode - modeMin)] =
                    {
                        'x': imode,
                        'y': deltaT,
                        'z': value,
//                        'style': value
                    };
                }
                ++itimeSlice;
            }
            else
            {
                for (var itime = 0; itime < (timeMax - 1); ++itime)
                {
                    timeStamp[itime] = timeStamp[itime + 1];
                    deltaT = (timeStamp[itime] - timeStamp[0]) / 1000;
                    for (var imode = modeMin; imode <= modeMax; ++imode)
                    {
                        oodataArray[itime * (modeMax - modeMin + 1) + (imode - modeMin)] =
                        {
                            'x': oodataArray[(itime + 1) * (modeMax - modeMin + 1) + (imode - modeMin)].x,
                            'y': deltaT,
                            'z': oodataArray[(itime + 1) * (modeMax - modeMin + 1) + (imode - modeMin)].z,
//                            'style': oodataArray[(itime + 1) * (modeMax - modeMin + 1) + (imode - modeMin)].style
                        };
                    }
                }
                timeStamp[timeMax - 1] = new Date().getTime();
                deltaT = (timeStamp[timeMax - 1] - timeStamp[0]) / 1000;
                for (var imode = modeMin; imode <= modeMax; ++imode)
                {
                    var value = Math.round(100.0 * Number(spectrumArray[imode])) / 100.0;
                    oodataArray[(timeMax - 1) * (modeMax - modeMin + 1) + (imode - modeMin)] =
                    {
                        'x': imode,
                        'y': deltaT,
                        'z': value,
//                        'style': value
                    };
                }
            }
            $("#startTime").text(new Date(timeStamp[0]).toLocaleString());
            $("#stopTime").text(new Date(timeStamp[itimeSlice - 1]).toLocaleString());
            graph3d.setData(oodataArray);
        }
        function freezeRun()
        {
            freeze = !freeze;
            if (freeze)
            {
                $("#freezeButton").text('Frozen');
            }
            else
            {
                $("#freezeButton").text('Running');
            }
        }
        function birdsEyeView()
        {
            var horzAngle = 315.0 * 3.1415927 / 180.0;
            var vertAngle = 45.0 * 3.1415927 / 180.0;
            graph3d.setCameraPosition({'horizontal': horzAngle, 'vertical': vertAngle, 'distance': 2.0});
            if (sliceViewActive)
            {
                timeMax =  birdsEyeTimeMax;   
                $("#plotslices").val(timeMax.toString());
            }
            sliceViewActive = false;
            birdsEyeViewActive = true;
            topViewActive = false;
        }
        function sliceView()
        {
            var horzAngle = 0.0 * 3.1415927 / 180.0;
            var vertAngle = 0.0 * 3.1415927 / 180.0;
            graph3d.setCameraPosition({'horizontal': horzAngle, 'vertical': vertAngle, 'distance': 1.1});
            timeMax = 2;
            $("#plotslices").val(timeMax.toString());
            oodataArray = [];
            itimeSlice = 0;
            initializeDataArray();
            graph3d.redraw();
            sliceViewActive = true;
            birdsEyeViewActive = false;
            topViewActive = false;
        }
        function topView()
        {
            var horzAngle = 0 * 3.1415927 / 180.0;
            var vertAngle = 90 * 3.1415927 / 180.0;
            graph3d.setCameraPosition({'horizontal': horzAngle, 'vertical': vertAngle, 'distance': 2.0});
            if (sliceViewActive)
            {
                timeMax =  birdsEyeTimeMax;   
                $("#plotslices").val(timeMax.toString());
            }
            sliceViewActive = false;
            birdsEyeViewActive = false;
            topViewActive = true;
        }
        function onCameraPositionChange(event) 
        {
            if (sliceViewActive) sliceView();
            if (birdsEyeViewActive) birdsEyeView();
            if (topViewActive) topView();
        }

