                    xaxis: 
                        {
                            tickfont: {color:valueColor, size:barConfig.textsize}
                        },
                    yaxis: 
                        {
                            range: [barConfig.lolo, barConfig.hihi],
                            tickfont: {color:barConfig.labelColor, size:barConfig.textsize}
                        },
                };

            Plotly.newPlot(barConfig.htmlId, data, layout, {'displayModeBar': false});
        }

// Horz Bar Widget
        function setHorzBarWidget(barConfig)
        {
            var lowValue = 0;
            var mediumValue = 0;
            var highValue = 0;

            var lowColor = barConfig.backgroundColor;
            var mediumColor = barConfig.backgroundColor;
            var highColor = barConfig.backgroundColor;
            var valueColor = barConfig.backgroundColor; 

            if ( barConfig.value <= barConfig.lolo)
            {
                lowValue = barConfig.lolo;
                lowColor = barConfig.lowColor;
                valueColor = lowColor;
            }
            if ( (barConfig.lolo <= barConfig.value) && (barConfig.value < barConfig.low) )
            {
                lowValue = barConfig.value;
                lowColor = barConfig.lowColor;
                valueColor = lowColor;
            }
            if ( (barConfig.low  <= barConfig.value) && (barConfig.value < barConfig.high) )
            {
                lowValue = barConfig.low;
                mediumValue = barConfig.value - barConfig.low; 
                lowColor = barConfig.mediumColor;
                mediumColor = barConfig.mediumColor;
                valueColor = mediumColor;

            }
            if ( (barConfig.high <= barConfig.value) && (barConfig.value < barConfig.hihi) )
            {
                lowValue = barConfig.low;
                mediumValue = barConfig.high - barConfig.low;
                highValue = barConfig.value - barConfig.high;
                lowColor = barConfig.highColor;
                mediumColor = barConfig.highColor;
                highColor = barConfig.highColor;
                valueColor = highColor;
            }
            if ( barConfig.hihi <= barConfig.value)
            {
                lowValue = barConfig.low;
                mediumValue = barConfig.high - barConfig.low; 
                highValue = barConfig.hihi - barConfig.high; 
                lowColor = barConfig.highColor;
                mediumColor = barConfig.highColor;
                highColor = barConfig.highColor;
                valueColor = highColor;
            }

            var lowBar = 
            {
                y: ['', '-'],
                x: [ barConfig.low, lowValue],
                type: 'bar',
                orientation: 'h',
                showlegend: false,
                marker:{color: [barConfig.lowColor, lowColor]},
              width: [0.1, 1.0],
            };
            var mediumBar = 
            {
                y: ['', '-'],
                x: [barConfig.high - barConfig.low, mediumValue],
                type: 'bar',
                orientation: 'h',
                showlegend: false,
                marker:{color: [barConfig.mediumColor, mediumColor]},
              width: [0.1, 1.0],
            };
            var highBar = 
            {
                y: ['', '-'],
                x: [barConfig.hihi - barConfig.high, highValue],
                orientation: 'h',
                type: 'bar',
                showlegend: false,
                marker:{color: [barConfig.highColor, highColor]},
              width: [0.1, 1.0],
            };
            var layout = 
                {
                    barmode: 'stack',
                    plot_bgcolor:barConfig.backgroundColor,
                    paper_bgcolor:barConfig.backgroundColor,
                    margin: 
                        {
                            t: 70, //top margin
                            l: 20, //left margin
                            r: 20, //right margin
                            b: 70 //bottom margin
                        },

                    height: barConfig.height,
                    title: 
                        {
                            text: barConfig.value.toString() + ' ' + barConfig.unit,
                            font: {color: valueColor, size:barConfig.textsize},
                        },
                    yaxis: 
                        {
                            tickfont: {color:valueColor, size:barConfig.textsize}
                        },
                    xaxis: 
                        {
                            range: [barConfig.lolo, barConfig.hihi],
                            tickfont: {color:barConfig.labelColor, size:barConfig.textsize},
                            gridcolor       : barConfig.gridColor
                        },
                };
           Plotly.newPlot(barConfig.htmlId, [lowBar, mediumBar, highBar], layout, {'displayModeBar': false});
        }
